const AllRequests = (props) => {
  const accept = async (val) => {
    try {
      const response = await fetch("http://localhost:5000/signUp", {
        method: "POST",
        body: JSON.stringify({
          name: val.username,
          email: val.email,
          password: val.password,
          role: val.role,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      });
    } catch (error) {
      console.error("Error:", error);
    }
    try {
      const response = await fetch(
        `http://localhost:5000/adminRequest/${val._id}`,
        {
          method: "DELETE",
        }
      );
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const reject = async (val) => {
    try {
      const response = await fetch(
        `http://localhost:5000/adminRequest/${val._id}`,
        {
          method: "DELETE",
        }
      );
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      {props.flag ? (
        <div>
          {props.productRequests.map((val, key) => (
            <div>
              <div>{val.name}</div>
              <div>{val.quantity}</div>
              <button>verification</button>
            </div>
          ))}
        </div>
      ) : (
        <div>
          {props.adminRequests.map((val, key) => (
            <div>
              <div>{val.name}</div>
              <div>{val.email}</div>
              <button onClick={accept(val)}>Confirm</button>
              <button onClick={reject(val)}>Reject</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AllRequests;
