import "./App.css";
import Signin from "./Pages/Signin";
import Signup from "./Pages/Signup";
import Home from "./Pages/Home";
import Dashboard from "./Pages/DashBoard";
import AboutUs from "./Pages/AboutUs";
import Product from "./Components/Product";

import { useState, useEffect } from "react";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import AddFolder from "./Components/AddFolder";
import AddProduct from "./Components/AddProduct";
import ProductDashBoard from "./Pages/ProductDashBoard";
import Summary from "./Components/Summary";
import CategorySummary from "./Components/CategorySummary";
import { ContactUs } from "./Components/ContactUs";

function App() {

  const [folders, setFolders] = useState([]);
  const [products, setProducts] = useState([]);
  const [amount, setAmount] = useState(0);

  useEffect(() => {

    fetch('http://localhost:5000/category', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((d) => setFolders(d))
      .catch((error) => console.error('Error:', error));
    
    for (let i = 0; i < folders.length; i++) {
      for (let j = 0; j < folders[i].data.length; j++) {
            setProducts(products=>[...products, folders[i].data[j]]);
        }
    }
  }, []);

  const getLoaclitems = () => {
    const name = localStorage.getItem("data");

    if (name) {
      return JSON.parse(name);
    } else return "";
  };

  const [data, setData] = useState(getLoaclitems());

  useEffect(() => {
    localStorage.setItem("data", JSON.stringify(data));
  }, [data]);

  return (
    <>
      <Router>
        <div>
          <Switch>
            <Route path="/signup">
              <Signup />
            </Route>
            <Route path="/signin">
              <Signin setData={setData} />
            </Route>
            <Route path="/dashboard">
              <Dashboard setData={setData} data={data} folders={folders} />
            </Route>
            <Route path="/products/:categoryId">
              <ProductDashBoard folders={folders} />
            </Route>
            <Route path="/addcategory">
              <AddFolder />
            </Route>
            <Route path="/addproduct/:categoryId">
              <AddProduct />
            </Route>
            <Route path="/product/:categoryId/:productId" folders={folders}>
              <Product />
            </Route>
            <Route path="/aboutus">
              <AboutUs />
            </Route>
            <Route path="/summary">
              <Summary products={products} />
            </Route>
            <Route path="/categories">
              <CategorySummary folders={folders} />
            </Route>
            <Route path="/contactus">
              <ContactUs />
            </Route>
            <Route path="/">
              <Home />
            </Route>
          </Switch>
        </div>
      </Router>
    </>
  );
}

export default App;
