import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck, faTimes } from "@fortawesome/free-solid-svg-icons";

const AllRequests = (props) => {
  return (
    <div className="bg-gray-100 text-gray-800 rounded-lg p-6 shadow-md">
      
        
        {props.flag ? (
          <tbody>
            {props.productRequests.map((val, key) => (
              <tr key={key}>
                <td className="px-4 py-2">{key + 1}</td>
                <td className="px-4 py-2">{val.name}</td>
                <td className="px-4 py-2">{val.email}</td>
                <td className="px-4 py-2">
                  <button>verification</button>
                </td>
              </tr>
            ))}
          </tbody>
        ) : (
          <table className="table-auto w-full">
          <thead>
          <tr>
            <th className="px-4 py-2">#</th>
            <th className="px-4 py-2">Name</th>
            <th className="px-4 py-2">Email</th>
            <th className="px-4 py-2">Action</th>
          </tr>
        </thead>
          <tbody>
            {props.adminRequests.map((val, key) => (
              <tr key={key}>
                <td className="px-4 py-2">{key + 1}</td>
                <td className="px-4 py-2">{val.name}</td>
                <td className="px-4 py-2">{val.email}</td>
                <td className="px-4 py-2">
                  <button
                    onClick={async () => {
                      try {
                        await fetch("http://localhost:5000/signUp", {
                          method: "POST",
                          body: JSON.stringify({
                            name: val.name,
                            email: val.email,
                            password: val.password,
                            role: val.role,
                          }),
                          headers: {
                            "Content-Type": "application/json",
                          },
                        });
                        // After successful signup, you might want to delete the admin request
                        await fetch(
                          `http://localhost:5000/adminRequest/${val._id}`,
                          {
                            method: "DELETE",
                            headers: {
                              "Content-Type": "application/json",
                            },
                          }
                        );
                      } catch (error) {
                        console.error("Error:", error);
                      }
                    }}
                    className="btn btn-success mr-2 hover:bg-green-500 hover:text-white transition duration-300 ease-in-out"
                  >
                    <FontAwesomeIcon icon={faCheck} />
                  </button>
                  <button
                    onClick={async () => {
                      try {
                        await fetch(
                          `http://localhost:5000/adminRequest/${val._id}`,
                          {
                            method: "DELETE",
                          }
                        );
                      } catch (error) {
                        console.error("Error:", error);
                      }
                    }}
                    className="btn btn-danger hover:bg-red-500 hover:text-white transition duration-300 ease-in-out"
                  >
                    <FontAwesomeIcon icon={faTimes} />
                  </button>
                </td>
              </tr>
            ))}
            </tbody>
            </table>
        )}
      
    </div>
  );
};

export default AllRequests;
